mapper.select:
SELECT
IFNULL(SUM(RP.point), 0) AS sum
FROM reactionPoint AS RP
WHERE
RP.relTypeCode = #{relTypeCode}
AND RP.relId = #{relId}
AND RP.memberId
= #{memberId}

mapper.delete:
DELETE FROM reactionPoint
WHERE memberId = #{memberId}
AND
relTypeCode =
#{relTypeCode}
AND relId = #{relId}

mapper.select:
SELECT
IFNULL(SUM(SP.point), 0) AS sum
FROM scrapPoint AS SP
WHERE
SP.relTypeCode = #{relTypeCode}
AND SP.relId = #{relId}
AND SP.memberId
= #{memberId}

mapper.delete:
DELETE FROM scrapPoint
WHERE memberId = #{memberId}
AND
relTypeCode =
#{relTypeCode}
AND relId = #{relId}
