mapper.select:
SELECT R.*
FROM recipe AS R
WHERE R.id = #{id}

SELECT R.*, M.nickname AS extra__writerName FROM recipe AS R LEFT JOIN `member` AS M ON R.memberId = M.id LEFT JOIN guide AS G ON G.id = R.guideId WHERE 1
AND G.sortId = #{relId}
AND G.methodId = #{relId}
AND G.contentId = #{relId}
AND G.freeId = #{relId}
